//create an object of key-value pairs
let contacts = {
    bff: 2736485,
    mom: 7777777,
    bro: 8729272,
}

//LOG ALL
console.log(contacts);

//access a spicific entry by its key (name)
console.log(contacts.bff);

//cant access if the key dosent exist
console.log(contacts.myself);

//add a mew emtry
contacts.sis =3456677;
console.log(contacts);

//update an entry
contacts.bff = 1111111;

console.log(contacts)

//delete an entry

delete contacts.mom;

console.log(contacts);

//is there a key called __ in there?
console.log("bff" in contacts);

console.log("myself" in contacts);

//objects are like arrays - pass by reference (location) not by value
//notice the following
let a ={
    value: 20
};
let b = a;
let c = {
    value: 20
};

console.log(a==b);
console.log(a==c);
console.log(b==c);

console.log(a.value == c.value);

